import * as React from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

export default function GeneratePasswordDialog() {
  const [open, setOpen] = React.useState(false);
  const [length, setLength] = React.useState<number|string>("");
  const [requestedPassword, setRequestedPassword] = React.useState<boolean>(false);
  const [error, setError] = React.useState<boolean>(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setRequestedPassword(false);
    setError(false);
  };
  const handleContinue = () => {
    if (length > 7 && length < 65) {
      setRequestedPassword(true);
    } else {
      setError(true);
    }
  };

  return (
    <div style={{ display: 'inline' }}>
      <Button variant="outlined" onClick={handleClickOpen}>
        New User
      </Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Password Generation</DialogTitle>
        {!requestedPassword && <DialogContent>
          <DialogContentText>
            Please choose length:
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            type='number'
            label="Password Length"
            fullWidth
            value={length}
            variant="standard"
            helperText={!error? "":"password length should be between 8 and 64 character, inclusive"}
            onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
              let value = parseInt(event.target.value)
              if (event.target.value == "" || value < 65) setLength(value)}}
          />
        </DialogContent>}
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleContinue}>Submit</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
